#[test]
fn parse_version_message() {
    let vec = ::hex::decode(VERSION_MESSAGE).unwrap();
    let message: Message = Deserialize::deserialize(&mut &vec[..]).unwrap();
    match message {
        Message::Version(_) => assert!(true),
        _ => assert!(false),
    };
}

