#[test]
fn reserialize_messages() {
    for message in MESSAGES.iter() {
        let vec = ::hex::decode(message).unwrap();
        let message: Message = Deserialize::deserialize(&mut &vec[..]).unwrap();
        assert!(message.serialize_to_vec() == vec);
    }
}
